    def arrange_trip(self):
        choice = input("What kind of place you'd like to go historical or to a beach?\n")
        if choice == 'historical':
            self.trip = VeniceTrip()
        elif choice == 'beach':
            self.trip = MaldivesTrip()
        self.trip.itinerary()


